mapper.select:
	SELECT USER_NUM, USER_ID, USER_PW, USER_NICK, USER_BIRTH, USER_PH, USER_NAME, USER_JOIN_DATE
    FROM TBL_USER
    WHERE USER_NUM = #{userNum}

SELECT COUNT(USER_NUM)
FROM TBL_STUDY
WHERE USER_NUM = #{userNum}

SELECT COUNT(USER_NUM)
FROM TBL_STUDY_APPLY
WHERE USER_NUM = #{userNum}

SELECT COUNT(USER_NUM)
FROM TBL_STUDY_COMMENT
WHERE USER_NUM = #{userNum}

SELECT COUNT(USER_NUM)
FROM TBL_BOARD
WHERE USER_NUM = #{userNum}

SELECT COUNT(USER_NUM)
FROM TBL_FB_COMMENT
WHERE USER_NUM = #{userNum}

SELECT * FROM ( SELECT ROWNUM AS rnum, noticeList.* FROM ( SELECT NOTICE_NUM, NOTICE_TITLE, NOTICE_CONTENTS, NOTICE_UPLOAD_DATE, NOTICE_EDIT_DATE FROM TBL_NOTICE
WHERE NOTICE_TITLE LIKE '%' || #{keyword} || '%' OR NOTICE_CONTENTS LIKE '%' || #{keyword} || '%'
ORDER BY NOTICE_NUM DESC ) noticeList WHERE ROWNUM <= #{endRow} ) WHERE rnum >= #{startRow}

mapper.update:
UPDATE TBL_NOTICE
SET NOTICE_TITLE=#{noticeTitle}, NOTICE_CONTENTS=#{noticeContents}, NOTICE_EDIT_DATE=SYSDATE 
WHERE NOTICE_NUM=#{noticeNum}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, studyApplyList.* FROM ( SELECT tsa.STUDY_APPLY_NUM, tsa.STUDY_NUM, tu.USER_NUM, tsa.STUDY_APPLY_STATUS, tu.USER_ID, tu.USER_NICK, (SELECT COUNT(tsa2.STUDY_APPLY_NUM) FROM TBL_STUDY_APPLY tsa2 WHERE tsa.STUDY_NUM = tsa2.STUDY_NUM) AS participants, (SELECT ts.STUDY_LIMIT FROM TBL_STUDY ts WHERE ts.STUDY_NUM = tsa.STUDY_NUM) AS limitParticipants, (SELECT ts2.STUDY_DEADLINE FROM TBL_STUDY ts2 WHERE ts2.STUDY_NUM = tsa.STUDY_NUM) AS deadline FROM TBL_STUDY_APPLY tsa INNER JOIN TBL_USER tu ON tsa.USER_NUM = tu.USER_NUM
WHERE tu.USER_ID LIKE '%' || #{keyword} || '%' OR tu.USER_NICK LIKE '%' || #{keyword} || '%'
ORDER BY STUDY_APPLY_NUM DESC ) studyApplyList WHERE ROWNUM <= #{endRow} ) WHERE rnum >= #{startRow}
